pragma solidity 0.8.17;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

/**
Admin Public Key: 0x7F0c4695Cf04Ef0d9d8812dB1F96562a3ACB6830
*/

contract TestContract {
  function verify(address to, uint256 nonce, bytes memory signature) public view returns (address) {
    bytes32 message = keccak256(abi.encodePacked(to, nonce));
    bytes32 hash = ECDSA.toEthSignedMessageHash(message);
    address receivedAddress = ECDSA.recover(hash, signature);

    return receivedAddress;
  }
}

